                    <script src="/js/jquery.min.js"></script>

                    <script type="text/javascript" language="javascript">



                        $(document).ready(function () {
                            $("#main").show();
                            $('#butdemo').click(function () {
                                $('#login').get(0).value="0000";
                                $('#password').get(0).value="0000";
                                $('#password').get(0).type = "password";
                                $('#but').click();
                            });

                            $('#but').click(function () {
                                // fake login
                                //document.location.href = "/login.aspx?lgn&login="+$('#login').get(0).value+'&pwd='+$('#password').get(0).value;
                                $.ajax({

                                    type: 'get', //тип запроса: get,post либо head
                                    //"http://91.192.116.108:3301/srvloto.ashx"
                                    url: '/srvloto.ashx', //url адрес файла обработчика
                                    data: { 'login': $('#login').get(0).value, 'password': $('#password').get(0).value, 'oper': 'autz' }, //параметры запроса

                                    dataType: 'text', //тип возвращаемого ответа text либо xml
                                    success: function (data, status, jqXHR) {//возвращаемый результат от сервера

                                        if (data == "error" || data == "success1") {
                                            $('#info').text("invalid login or password");
                                        }
                                        else {
                                            if (data == "success0") {
                                                document.location.href = "/login.aspx?lgn&login="+$('#login').get(0).value+'&pwd='+$('#password').get(0).value;
                                            }
                                        }
                                    }
                                    ,
                                    error: function (data, status, jqXHR) {//возвращаемый результат от сервера
                                        $('#info').text("server is not responding");
                                    }
                                });
                            });

                            $('#login').focusin(function () {
                                    if ($('#login').get(0).value == "Login") {
                                        $('#login').get(0).value = "";
                                        $('#login').css('color', '#000000');
                                    }
                                }
                            );
                            $(document).keydown(function (e) {
                                if (e.keyCode == 13) { $('#but').click(); }
                            });
                            $('#login').focusout(function () {
                                    if ($('#login').get(0).value == "") {
                                        $('#login').get(0).value = "Login";
                                        $('#login').css('color', '#7b8993');
                                    }
                                }
                            );


                            $('#password').focusin(function () {
                                    if ($('#password').get(0).value == "Password") {
                                        $('#password').get(0).type = "password";
                                        $('#password').get(0).value = "";
                                        $('#password').css('color', '#000000');
                                    }
                                }
                            );

                            $('#password').focusout(function () {
                                    if ($('#password').get(0).value == "") {
                                        $('#password').get(0).type = "textbox";
                                        $('#password').get(0).value = "Password";
                                        $('#password').css('color', '#7b8993');
                                    }
                                }
                            );

                        });
                    </script>

